public int[] TwoSum(int[] nums, int target) {
    var map = new Dictionary<int, int>();

    for(int i = 0; i < nums.Length; i++)
    {
        int difference = target - nums[i];

        if (map.ContainsKey(difference))
            return [i, map[difference]];

        if (!map.ContainsKey(nums[i]))
            map.Add(nums[i], i);
    }

    return [];
}
